    public void teleopPeriodic() {
    	GamepadState state = GamepadState.makeState(oi.gamepad);
    	
    	// Recordable Autonomous
    	state = updateRecordState(state);
    	
    	handleInput(state);
    	
        Scheduler.getInstance().run();
    }

